import React, { useState, useEffect } from "react";

const Onscreen = (props) => {
    const [totaldata, setTotalData] = useState(props);

    // Update totaldata when props change
    useEffect(() => {
        setTotalData(props);
    }, [props]);

    const deleteHandler = (item) => {
        setTotalData(totaldata.filter((_, i) => i !== item));
    };

    return (
        <div>
            {Array.isArray(totaldata) && totaldata.map((ele, item) => (
                <div key={item}>
                    <p>{ele.Name}</p>
                    <p>{ele.Bus}</p>
                    <p>{ele.Email}</p>
                    <p>{ele.Phone}</p>
                    <button onClick={() => deleteHandler(item)}>Delete</button>
                    <button>Edit</button>
                </div>
            ))}
        </div>
    );
};

export default Onscreen;
