import React, { useState, useRef } from "react";
import Onscreen from "./Onscreen";

const Form = () => {
    const [entereddata, setentereddata] = useState([]);
    const [submitted, setsubmitted] = useState(false);
    const form_data={}



    const handleFormSubmit = (event) => {
        event.preventDefault();

        form_data = {
            Name: event.target.elements.name.value,
            Bus: event.target.elements.selector.value,
            Email:event.target.elements.email.value,
            Phone:event.target.elements.number.value
        };

        setentereddata([form_data,...entereddata]);
        setsubmitted(true);

    };
    console.log(typeof(entereddata))
    const editHandler=(ele)=>{
        form_data.Name = ele.Name
        form_data.Bus = ele.Bus
        form_data.Email=ele.Email
        form_data.Phone=ele.Phone


    }

    return (
        <div>
            <form onSubmit={handleFormSubmit}>
                <h1>BUS Booking</h1>
                
                <label htmlFor="name">Name</label>
                <input type='text' id='name'></input>
                <select id='selector'>
                    <option id='Bus1' value='suresh'>BUS1</option>
                    <option id="BUs2" value='deepank'>BUS2</option>
                    <option id="Bus3" value='abhik'>Bus3</option>
                </select>
                <label htmlFor="number">PHONE</label>
                <input type='number' id='number'></input>
                <label htmlFor="email">Email</label>
                <input id='email' type='email'></input>
                <button type='submit'>Vote</button>


            </form>
            {submitted && <Onscreen totaldata={entereddata} onEdit={editHandler(ele)} />}
        </div>
    );
};

export default Form;
